import { useQuery } from '@tanstack/react-query';
import { AppLayout } from '@/components/layout/AppLayout';
import { Breadcrumb } from '@/components/layout/Breadcrumb';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { useAuth } from '@/lib/auth';
import { Skeleton } from '@/components/ui/skeleton';
import { Badge } from '@/components/ui/badge';
import { Clock } from 'lucide-react';

const DAYS = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
const PERIODS = [
  { label: 'Period 1', time: '08:00 - 09:00' },
  { label: 'Period 2', time: '09:00 - 10:00' },
  { label: 'Period 3', time: '10:00 - 11:00' },
  { label: 'Break', time: '11:00 - 11:15' },
  { label: 'Period 4', time: '11:15 - 12:15' },
  { label: 'Period 5', time: '12:15 - 01:15' },
  { label: 'Lunch', time: '01:15 - 02:00' },
  { label: 'Period 6', time: '02:00 - 03:00' },
];

export default function Timetable() {
  const { user } = useAuth();

  const { data: timetableData, isLoading } = useQuery({
    queryKey: user?.role === 'student' ? ['/api/student/timetable'] : ['/api/classes'],
    enabled: !!user,
  });

  const timetable = timetableData?.timetable || [];

  const getTimetableForDayAndPeriod = (dayIndex: number, periodIndex: number) => {
    return timetable.find(
      (item: any) => item.day === dayIndex && item.period === periodIndex
    );
  };

  const isBreakPeriod = (periodLabel: string) => {
    return periodLabel === 'Break' || periodLabel === 'Lunch';
  };

  return (
    <AppLayout>
      <div className="p-6 space-y-6">
        <Breadcrumb items={[{ label: 'Timetable' }]} />

        <div>
          <h1 className="text-3xl font-semibold">Class Timetable</h1>
          <p className="text-muted-foreground mt-1">Weekly schedule at a glance</p>
        </div>

        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>Weekly Schedule</CardTitle>
                <CardDescription>All classes and periods for the week</CardDescription>
              </div>
              <Clock className="h-5 w-5 text-muted-foreground" />
            </div>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="space-y-4">
                {[1, 2, 3, 4].map((i) => (
                  <Skeleton key={i} className="h-16 w-full" />
                ))}
              </div>
            ) : (
              <div className="overflow-x-auto">
                <div className="min-w-[1200px]">
                  <div className="grid grid-cols-7 gap-2 mb-2">
                    <div className="font-semibold text-sm text-center p-2">Period / Day</div>
                    {DAYS.map((day) => (
                      <div
                        key={day}
                        className="font-semibold text-sm text-center p-2 bg-primary/5 rounded"
                        data-testid={`header-${day.toLowerCase()}`}
                      >
                        {day}
                      </div>
                    ))}
                  </div>

                  {PERIODS.map((period, periodIndex) => (
                    <div
                      key={period.label}
                      className="grid grid-cols-7 gap-2 mb-2"
                      data-testid={`row-${period.label.toLowerCase().replace(' ', '-')}`}
                    >
                      <div
                        className={`text-sm p-3 rounded border ${
                          isBreakPeriod(period.label)
                            ? 'bg-muted/50'
                            : 'bg-background'
                        }`}
                      >
                        <div className="font-medium">{period.label}</div>
                        <div className="text-xs text-muted-foreground">{period.time}</div>
                      </div>

                      {DAYS.map((_, dayIndex) => {
                        if (isBreakPeriod(period.label)) {
                          return (
                            <div
                              key={dayIndex}
                              className="text-sm p-3 rounded border bg-muted/30 flex items-center justify-center"
                              data-testid={`cell-day${dayIndex}-period${periodIndex}`}
                            >
                              <span className="text-muted-foreground text-xs">
                                {period.label}
                              </span>
                            </div>
                          );
                        }

                        const classItem = getTimetableForDayAndPeriod(dayIndex, periodIndex);

                        return (
                          <div
                            key={dayIndex}
                            className={`text-sm p-3 rounded border ${
                              classItem
                                ? 'bg-blue-50 dark:bg-blue-950/20 hover:bg-blue-100 dark:hover:bg-blue-950/30 cursor-pointer transition-colors'
                                : 'bg-background hover:bg-muted/50'
                            }`}
                            data-testid={`cell-day${dayIndex}-period${periodIndex}`}
                          >
                            {classItem ? (
                              <div className="space-y-1">
                                <div className="font-medium text-primary">
                                  {classItem.subjectId?.name || 'Subject'}
                                </div>
                                {classItem.subjectId?.code && (
                                  <Badge variant="outline" className="text-xs">
                                    {classItem.subjectId.code}
                                  </Badge>
                                )}
                                <div className="text-xs text-muted-foreground">
                                  {classItem.teacherId
                                    ? `${classItem.teacherId.firstName} ${classItem.teacherId.lastName}`
                                    : 'Teacher TBA'}
                                </div>
                                {classItem.room && (
                                  <div className="text-xs text-muted-foreground">
                                    Room {classItem.room}
                                  </div>
                                )}
                              </div>
                            ) : (
                              <div className="text-xs text-muted-foreground text-center">
                                Free
                              </div>
                            )}
                          </div>
                        );
                      })}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Legend</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex flex-wrap gap-4">
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 rounded bg-blue-100 dark:bg-blue-950/20 border"></div>
                <span className="text-sm">Scheduled Class</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 rounded bg-muted/30 border"></div>
                <span className="text-sm">Break / Lunch</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 rounded bg-background border"></div>
                <span className="text-sm">Free Period</span>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </AppLayout>
  );
}

client/src/pages/Academics.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { AppLayout } from '@/components/layout/AppLayout';
import { Breadcrumb } from '@/components/layout/Breadcrumb';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Plus } from 'lucide-react';
import { DataTable } from '@/components/shared/DataTable';
import { Badge } from '@/components/ui/badge';
import { useAuth } from '@/lib/auth';

interface Class {
  _id: string;
  name: string;
  grade: number;
  section: string;
  capacity: number;
  classTeacherId?: string;
  academicYear: string;
}

interface Subject {
  _id: string;
  name: string;
  code: string;
  description?: string;
}

export default function Academics() {
  const { user } = useAuth();
  const canManageAcademics = user && ['admin', 'principal'].includes(user.role);

  const { data: classesData, isLoading: classesLoading } = useQuery<{ classes: Class[] }>({
    queryKey: ['/api/classes'],
  });

  const { data: subjectsData, isLoading: subjectsLoading } = useQuery<{ subjects: Subject[] }>({
    queryKey: ['/api/subjects'],
  });

  const classes = classesData?.classes || [];
  const subjects = subjectsData?.subjects || [];

  return (
    <AppLayout>
      <div className="p-6 space-y-6 max-w-7xl">
        <Breadcrumb items={[{ label: 'Academics' }]} />

        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-semibold">Academic Management</h1>
            <p className="text-muted-foreground mt-1">Manage classes, subjects, and curriculum</p>
          </div>
        </div>

        <Tabs defaultValue="classes" className="space-y-6">
          <TabsList>
            <TabsTrigger value="classes" data-testid="tab-classes">Classes</TabsTrigger>
            <TabsTrigger value="subjects" data-testid="tab-subjects">Subjects</TabsTrigger>
          </TabsList>

          <TabsContent value="classes" className="space-y-6">
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle>All Classes</CardTitle>
                    <CardDescription>Manage class sections and capacity</CardDescription>
                  </div>
                  {canManageAcademics && (
                    <Button data-testid="button-add-class">
                      <Plus className="mr-2 h-4 w-4" />
                      Add Class
                    </Button>
                  )}
                </div>
              </CardHeader>
              <CardContent>
                {classesLoading ? (
                  <div className="text-center py-8 text-muted-foreground">Loading classes...</div>
                ) : (
                  <DataTable
                    data={classes}
                    emptyMessage="No classes found. Add a class to get started."
                    columns={[
                      {
                        key: 'name',
                        header: 'Class Name',
                        cell: (item) => (
                          <div>
                            <p className="font-medium">{item.name}</p>
                            <p className="text-sm text-muted-foreground">Grade {item.grade} - Section {item.section}</p>
                          </div>
                        ),
                      },
                      {
                        key: 'capacity',
                        header: 'Capacity',
                        cell: (item) => (
                          <div>
                            <p className="font-medium">{item.capacity} students</p>
                          </div>
                        ),
                      },
                      {
                        key: 'academicYear',
                        header: 'Academic Year',
                        cell: (item) => <span>{item.academicYear}</span>,
                      },
                      {
                        key: 'status',
                        header: 'Status',
                        cell: () => (
                          <Badge variant="default">Active</Badge>
                        ),
                      },
                      {
                        key: 'actions',
                        header: 'Actions',
                        cell: () => canManageAcademics ? (
                          <div className="flex gap-2">
                            <Button variant="ghost" size="sm">Edit</Button>
                            <Button variant="ghost" size="sm">View</Button>
                          </div>
                        ) : null,
                      },
                    ]}
                    testId="classes-table"
                  />
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="subjects" className="space-y-6">
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle>All Subjects</CardTitle>
                    <CardDescription>Manage subject curriculum and assignments</CardDescription>
                  </div>
                  {canManageAcademics && (
                    <Button data-testid="button-add-subject">
                      <Plus className="mr-2 h-4 w-4" />
                      Add Subject
                    </Button>
                  )}
                </div>
              </CardHeader>
              <CardContent>
                {subjectsLoading ? (
                  <div className="text-center py-8 text-muted-foreground">Loading subjects...</div>
                ) : (
                  <DataTable
                    data={subjects}
                    emptyMessage="No subjects found. Add a subject to get started."
                    columns={[
                      {
                        key: 'subject',
                        header: 'Subject',
                        cell: (item) => (
                          <div>
                            <p className="font-medium">{item.name}</p>
                            <p className="text-sm text-muted-foreground font-mono">{item.code}</p>
                          </div>
                        ),
                      },
                      {
                        key: 'description',
                        header: 'Description',
                        cell: (item) => <span>{item.description || 'N/A'}</span>,
                      },
                      {
                        key: 'actions',
                        header: 'Actions',
                        cell: () => canManageAcademics ? (
                          <div className="flex gap-2">
                            <Button variant="ghost" size="sm">Edit</Button>
                            <Button variant="ghost" size="sm">Assign</Button>
                          </div>
                        ) : null,
                      },
                    ]}
                    testId="subjects-table"
                  />
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </AppLayout>
  );
}

