import * as React from "react"
import { OTPInput, OTPInputContext } from "input-otp"
import { Dot } from "lucide-react"

import { cn } from "@/lib/utils"

const InputOTP = React.forwardRef<
  React.ElementRef<typeof OTPInput>,
  React.ComponentPropsWithoutRef<typeof OTPInput>
>(({ className, containerClassName, ...props }, ref) => (
  <OTPInput
    ref={ref}
    containerClassName={cn(
      "flex items-center gap-2 has-[:disabled]:opacity-50",
      containerClassName
    )}
    className={cn("disabled:cursor-not-allowed", className)}
    {...props}
  />
))
InputOTP.displayName = "InputOTP"

const InputOTPGroup = React.forwardRef<
  React.ElementRef<"div">,
  React.ComponentPropsWithoutRef<"div">
>(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("flex items-center", className)} {...props} />
))
InputOTPGroup.displayName = "InputOTPGroup"

const InputOTPSlot = React.forwardRef<
  React.ElementRef<"div">,
  React.ComponentPropsWithoutRef<"div"> & { index: number }
>(({ index, className, ...props }, ref) => {
  const inputOTPContext = React.useContext(OTPInputContext)
  const { char, hasFakeCaret, isActive } = inputOTPContext.slots[index]

  return (
    <div
      ref={ref}
      className={cn(
        "relative flex h-10 w-10 items-center justify-center border-y border-r border-input text-sm transition-all first:rounded-l-md first:border-l last:rounded-r-md",
        isActive && "z-10 ring-2 ring-ring ring-offset-background",
        className
      )}
      {...props}
    >
      {char}
      {hasFakeCaret && (
        <div className="pointer-events-none absolute inset-0 flex items-center justify-center">
          <div className="h-4 w-px animate-caret-blink bg-foreground duration-1000" />
        </div>
      )}
    </div>
  )
})
InputOTPSlot.displayName = "InputOTPSlot"

const InputOTPSeparator = React.forwardRef<
  React.ElementRef<"div">,
  React.ComponentPropsWithoutRef<"div">
>(({ ...props }, ref) => (
  <div ref={ref} role="separator" {...props}>
    <Dot />
  </div>
))
InputOTPSeparator.displayName = "InputOTPSeparator"

export { InputOTP, InputOTPGroup, InputOTPSlot, InputOTPSeparator }

client/src/pages/Fees.tsx.content:
import { useQuery } from '@tanstack/react-query';
import { AppLayout } from '@/components/layout/AppLayout';
import { Breadcrumb } from '@/components/layout/Breadcrumb';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Plus, Download, IndianRupee } from 'lucide-react';
import { DataTable } from '@/components/shared/DataTable';
import { Badge } from '@/components/ui/badge';
import { StatCard } from '@/components/shared/StatCard';
import { TrendingUp, AlertCircle, CreditCard } from 'lucide-react';
import { formatCurrencyINR } from '@/lib/utils';

interface FeePayment {
  id: string;
  student: string;
  class: string;
  amount: number;
  status: string;
  date: string;
  receipt: string;
}

interface FeeStructure {
  _id: string;
  name: string;
  amount: number;
  classId: string;
  dueDate?: string;
  description?: string;
}

export default function Fees() {
  const { data: paymentsData, isLoading: paymentsLoading } = useQuery<{ payments: FeePayment[] }>({
    queryKey: ['/api/fee-payments'],
  });

  const { data: structuresData, isLoading: structuresLoading } = useQuery<{ feeStructures: FeeStructure[] }>({
    queryKey: ['/api/fee-structures'],
  });

  const feePayments = paymentsData?.payments || [];
  const feeStructures = structuresData?.feeStructures || [];

  const totalCollected = feePayments
    .filter(p => p.status === 'paid')
    .reduce((sum, p) => sum + p.amount, 0);

  const pendingAmount = feePayments
    .filter(p => p.status === 'pending')
    .reduce((sum, p) => sum + p.amount, 0);

  const overdueAmount = feePayments
    .filter(p => p.status === 'overdue')
    .reduce((sum, p) => sum + p.amount, 0);

  const totalAmount = totalCollected + pendingAmount + overdueAmount;
  const collectionRate = totalAmount > 0 ? (totalCollected / totalAmount) * 100 : 0;

  return (
    <AppLayout>
      <div className="p-6 space-y-6 max-w-7xl">
        <Breadcrumb items={[{ label: 'Fee Management' }]} />

        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-semibold">Fee Management</h1>
            <p className="text-muted-foreground mt-1">Track fee collection and payment status</p>
          </div>
          <Button variant="outline" data-testid="button-export-fees">
            <Download className="mr-2 h-4 w-4" />
            Export Report
          </Button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <StatCard
            title="Total Collected"
            value={formatCurrencyINR(totalCollected)}
            icon={IndianRupee}
            testId="stat-total-collected"
          />
          <StatCard
            title="Pending Payments"
            value={formatCurrencyINR(pendingAmount)}
            icon={AlertCircle}
            testId="stat-pending-payments"
          />
          <StatCard
            title="Overdue"
            value={formatCurrencyINR(overdueAmount)}
            icon={TrendingUp}
            testId="stat-overdue"
          />
          <StatCard
            title="Collection Rate"
            value={`${collectionRate.toFixed(1)}%`}
            icon={CreditCard}
            testId="stat-collection-rate"
          />
        </div>

        <Tabs defaultValue="payments" className="space-y-6">
          <TabsList>
            <TabsTrigger value="payments" data-testid="tab-payments">Payments</TabsTrigger>
            <TabsTrigger value="structures" data-testid="tab-structures">Fee Structures</TabsTrigger>
          </TabsList>

          <TabsContent value="payments" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Recent Payments</CardTitle>
                <CardDescription>Latest fee payments and pending amounts</CardDescription>
              </CardHeader>
              <CardContent>
                {paymentsLoading ? (
                  <div className="text-center py-8 text-muted-foreground">Loading payments...</div>
                ) : (
                  <DataTable
                    data={feePayments}
                    emptyMessage="No payments found"
                    columns={[
                      {
                        key: 'student',
                        header: 'Student',
                        cell: (item) => (
                          <div>
                            <p className="font-medium">{item.student}</p>
                            <p className="text-sm text-muted-foreground">{item.class}</p>
                          </div>
                        ),
                      },
                      {
                        key: 'amount',
                        header: 'Amount',
                        cell: (item) => <span className="font-mono font-medium">{formatCurrencyINR(item.amount)}</span>,
                      },
                      {
                        key: 'date',
                        header: 'Date',
                        cell: (item) => <span className="text-sm">{item.date}</span>,
                      },
                      {
                        key: 'status',
                        header: 'Status',
                        cell: (item) => (
                          <Badge
                            variant={
                              item.status === 'paid' ? 'default' :
                              item.status === 'pending' ? 'secondary' :
                              'destructive'
                            }
                          >
                            {item.status}
                          </Badge>
                        ),
                      },
                      {
                        key: 'receipt',
                        header: 'Receipt',
                        cell: (item) => (
                          item.receipt ? (
                            <Button variant="ghost" size="sm">
                              View
                            </Button>
                          ) : (
                            <span className="text-sm text-muted-foreground">-</span>
                          )
                        ),
                      },
                    ]}
                  />
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="structures" className="space-y-6">
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle>Fee Structures</CardTitle>
                    <CardDescription>Manage fee types and amounts</CardDescription>
                  </div>
                  <Button data-testid="button-add-fee-structure">
                    <Plus className="mr-2 h-4 w-4" />
                    Add Fee Structure
                  </Button>
                </div>
              </CardHeader>
              <CardContent>
                {structuresLoading ? (
                  <div className="text-center py-8 text-muted-foreground">Loading fee structures...</div>
                ) : (
                  <DataTable
                    data={feeStructures}
                    emptyMessage="No fee structures found. Add one to get started."
                    columns={[
                      {
                        key: 'name',
                        header: 'Fee Name',
                        cell: (item) => (
                          <div>
                            <p className="font-medium">{item.name}</p>
                            {item.description && (
                              <p className="text-sm text-muted-foreground">{item.description}</p>
                            )}
                          </div>
                        ),
                      },
                      {
                        key: 'amount',
                        header: 'Amount',
                        cell: (item) => <span className="font-mono font-medium">{formatCurrencyINR(item.amount)}</span>,
                      },
                      {
                        key: 'dueDate',
                        header: 'Due Date',
                        cell: (item) => (
                          <span className="text-sm">
                            {item.dueDate ? new Date(item.dueDate).toLocaleDateString() : 'N/A'}
                          </span>
                        ),
                      },
                      {
                        key: 'actions',
                        header: 'Actions',
                        cell: () => (
                          <div className="flex gap-2">
                            <Button variant="ghost" size="sm">Edit</Button>
                            <Button variant="ghost" size="sm">Delete</Button>
                          </div>
                        ),
                      },
                    ]}
                  />
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </AppLayout>
  );
}

client/src/pages/SupportTickets.tsx.content:
import { useState } from 'react';
import { AppLayout } from '@/components/layout/AppLayout';
import { Breadcrumb } from '@/components/layout/Breadcrumb';
import { DataTable } from '@/components/shared/DataTable';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Eye, MessageSquare } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import { useAuth } from '@/lib/auth';
import { useToast } from '@/hooks/use-toast';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';

export default function SupportTickets() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [selectedTicket, setSelectedTicket] = useState<any>(null);
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false);

  const canManageTickets = user && user.role === 'super_admin';

  const tickets = [
    {
      id: '1',
      ticketId: 'TKT-001',
      school: 'Springfield High School',
      title: 'Unable to add new students',
      description: 'Getting an error when trying to add new student records through the student management module.',
      category: 'Technical',
      priority: 'high',
      status: 'open',
      createdBy: 'Admin User',
      createdAt: '2025-01-26 09:30 AM',
      assignedTo: null,
    },
    {
      id: '2',
      ticketId: 'TKT-002',
      school: 'Riverside Academy',
      title: 'Request for additional faculty licenses',
      description: 'We need to add 5 more faculty members but have reached our subscription limit.',
      category: 'Billing',
      priority: 'medium',
      status: 'in_progress',
      createdBy: 'Principal',
      createdAt: '2025-01-25 02:15 PM',
      assignedTo: 'Support Team',
    },
    {
      id: '3',
      ticketId: 'TKT-003',
      school: 'Greenwood International',
      title: 'Fee payment gateway not working',
      description: 'Parents are reporting that they cannot make online payments. The payment gateway returns an error.',
      category: 'Technical',
      priority: 'urgent',
      status: 'open',
      createdBy: 'Finance Manager',
      createdAt: '2025-01-27 11:45 AM',
      assignedTo: null,
    },
    {
      id: '4',
      ticketId: 'TKT-004',
      school: 'Oakdale School',
      title: 'Question about exam module features',
      description: 'Need clarification on how to set up different grading scales for different subjects.',
      category: 'Support',
      priority: 'low',
      status: 'resolved',
      createdBy: 'Admin',
      createdAt: '2025-01-24 10:00 AM',
      assignedTo: 'Support Team',
      resolvedAt: '2025-01-25 03:30 PM',
    },
  ];

  const handleViewTicket = (ticket: any) => {
    setSelectedTicket(ticket);
    setIsViewDialogOpen(true);
  };

  const handleAssignTicket = (ticket: any) => {
    toast({
      title: 'Ticket Assigned',
      description: `Ticket ${ticket.ticketId} has been assigned to support team.`,
    });
  };

  const handleResolveTicket = (ticket: any) => {
    toast({
      title: 'Ticket Resolved',
      description: `Ticket ${ticket.ticketId} has been marked as resolved.`,
    });
  };

  const openTickets = tickets.filter(t => t.status === 'open');
  const inProgressTickets = tickets.filter(t => t.status === 'in_progress');
  const resolvedTickets = tickets.filter(t => t.status === 'resolved');

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return 'destructive';
      case 'high':
        return 'default';
      case 'medium':
        return 'secondary';
      case 'low':
        return 'outline';
      default:
        return 'outline';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'open':
        return 'destructive';
      case 'in_progress':
        return 'default';
      case 'resolved':
        return 'secondary';
      default:
        return 'outline';
    }
  };

  return (
    <AppLayout>
      <div className="p-6 space-y-6 max-w-7xl">
        <Breadcrumb items={[{ label: 'Support Tickets' }]} />

        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-semibold">Support Tickets</h1>
            <p className="text-muted-foreground mt-1">Manage support tickets from all schools</p>
          </div>
          <div className="flex items-center gap-2">
            <Select defaultValue="all">
              <SelectTrigger className="w-48" data-testid="select-filter-priority">
                <SelectValue placeholder="Filter by Priority" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Priorities</SelectItem>
                <SelectItem value="urgent">Urgent</SelectItem>
                <SelectItem value="high">High</SelectItem>
                <SelectItem value="medium">Medium</SelectItem>
                <SelectItem value="low">Low</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card>
            <CardContent className="pt-6">
              <div className="text-center">
                <p className="text-3xl font-bold">{tickets.length}</p>
                <p className="text-sm text-muted-foreground mt-1">Total Tickets</p>
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="pt-6">
              <div className="text-center">
                <p className="text-3xl font-bold text-red-600">{openTickets.length}</p>
                <p className="text-sm text-muted-foreground mt-1">Open</p>
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="pt-6">
              <div className="text-center">
                <p className="text-3xl font-bold text-blue-600">{inProgressTickets.length}</p>
                <p className="text-sm text-muted-foreground mt-1">In Progress</p>
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="pt-6">
              <div className="text-center">
                <p className="text-3xl font-bold text-green-600">{resolvedTickets.length}</p>
                <p className="text-sm text-muted-foreground mt-1">Resolved</p>
              </div>
            </CardContent>
          </Card>
        </div>

        <Tabs defaultValue="all" className="space-y-6">
          <TabsList>
            <TabsTrigger value="all" data-testid="tab-all-tickets">All ({tickets.length})</TabsTrigger>
            <TabsTrigger value="open" data-testid="tab-open">Open ({openTickets.length})</TabsTrigger>
            <TabsTrigger value="in-progress" data-testid="tab-in-progress">In Progress ({inProgressTickets.length})</TabsTrigger>
            <TabsTrigger value="resolved" data-testid="tab-resolved">Resolved ({resolvedTickets.length})</TabsTrigger>
          </TabsList>

          <TabsContent value="all" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>All Tickets</CardTitle>
              </CardHeader>
              <CardContent>
                <DataTable
                  data={tickets}
                  emptyMessage="No tickets found"
                  columns={[
                    {
                      key: 'ticket',
                      header: 'Ticket',
                      cell: (item) => (
                        <div>
                          <p className="font-medium">{item.ticketId}</p>
                          <p className="text-sm text-muted-foreground">{item.title}</p>
                        </div>
                      ),
                    },
                    {
                      key: 'school',
                      header: 'School',
                      cell: (item) => item.school,
                    },
                    {
                      key: 'category',
                      header: 'Category',
                      cell: (item) => item.category,
                    },
                    {
                      key: 'priority',
                      header: 'Priority',
                      cell: (item) => (
                        <Badge variant={getPriorityColor(item.priority)}>
                          {item.priority}
                        </Badge>
                      ),
                    },
                    {
                      key: 'status',
                      header: 'Status',
                      cell: (item) => (
                        <Badge variant={getStatusColor(item.status)}>
                          {item.status.replace('_', ' ')}
                        </Badge>
                      ),
                    },
                    {
                      key: 'created',
                      header: 'Created',
                      cell: (item) => (
                        <div>
                          <p className="text-sm">{item.createdAt}</p>
                          <p className="text-xs text-muted-foreground">by {item.createdBy}</p>
                        </div>
                      ),
                    },
                    {
                      key: 'actions',
                      header: 'Actions',
                      cell: (item) => canManageTickets ? (
                        <div className="flex items-center gap-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleViewTicket(item)}
                            data-testid={`button-view-ticket-${item.id}`}
                          >
                            <Eye className="h-4 w-4" />
                          </Button>
                          {item.status === 'open' && (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => handleAssignTicket(item)}
                              data-testid={`button-assign-ticket-${item.id}`}
                            >
                              Assign
                            </Button>
                          )}
                          {item.status === 'in_progress' && (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => handleResolveTicket(item)}
